          dataSource={ownerEvents}
          renderItem={(item) => {
            return (
              <List.Item key={"owner_"+item[0]}>
              <Address
                address={item[0]}
                fontSize={32}
              />
              <div style={{padding:16}}>
                {item[1]?"üëç":"üëé"}
              </div>
              </List.Item>
              )
            }}
          />

      </div>
    </div>
  );
}
